import React, { useState, useEffect } from 'react';
import Head from 'next/head';
import fetch from 'isomorphic-unfetch';

//https://next-mongodb-nine.now.sh/api/daily
//http://localhost:3000/api/daily'

//const apiUrl = 'https://next-mongodb-nine.now.sh/api/daily';
const apiUrl = 'http://localhost:3000/api/groceries';

const Groceries = ({ groceries }) => {
    return (
        <div>
            Groceries Page
        </div>
    );
}

Groceries.getInitialProps = async ({query, req}) => {
    //https://next-mongodb-nine.now.sh/api/daily
    //http://localhost:3000/api/daily'
    if(!req) {
        return { data: undefined };
    }

    //query.person - get the person param from the query string

    const res = await fetch(apiUrl)
    const json = await res.json()
    return { groceries: json }
}


export default Groceries;
